require 'test/unit'
require_relative 'solution'

class LongestPalindromeTest < Test::Unit::TestCase
  @@test_case1 = 'babad'
  @@test_case2 = 'cbbd'
  @@test_case3 = 'a'
  @@test_case4 = ''
  @@test_case5 = ''
  @@test_case6 = 'aaabaaaa'

  def test_case_1
    assert_equal 'aba', longest_palindrome(@@test_case1)
  end

  def test_case_2
    assert_equal 'bb', longest_palindrome(@@test_case2)
  end

  def test_case_3
    assert_equal 'a', longest_palindrome(@@test_case3)
  end

  def test_case_4
    assert_equal @@test_case4, longest_palindrome(@@test_case4)
  end

  def test_case_5
    expected = ''
    assert_equal expected, longest_palindrome(@@test_case5)
  end

  def test_case_6
    expected = 'aaabaaa'
    assert_equal expected, longest_palindrome(@@test_case6)
  end
end
